import { TestBed } from '@angular/core/testing';
import { AppliedJobsComponent } from './applied-jobs.component';
import { ReactiveFormsModule } from '@angular/forms';
import { RouterTestingModule } from '@angular/router/testing';
import { HttpClientTestingModule } from '@angular/common/http/testing';
import { ToastrModule, ToastrService } from 'ngx-toastr';
import { AppliedJobsService } from './applied-jobs.service';
describe('AppliedJobsComponent', () => {
    let component;
    let fixture;
    let appliedJobsService;
    let toastrService;
    beforeEach(async () => {
        const appliedJobsServiceSpy = jasmine.createSpyObj('AppliedJobsService', [
            'getAllAppliedJobs',
            'getEmployer',
            'getJob'
        ]);
        const toastrServiceSpy = jasmine.createSpyObj('ToastrService', ['error']);
        await TestBed.configureTestingModule({
            imports: [
                ReactiveFormsModule,
                RouterTestingModule,
                HttpClientTestingModule,
                ToastrModule.forRoot()
            ],
            declarations: [AppliedJobsComponent],
            providers: [
                { provide: AppliedJobsService, useValue: appliedJobsServiceSpy },
                { provide: ToastrService, useValue: toastrServiceSpy }
            ]
        })
            .compileComponents();
    });
    beforeEach(() => {
        fixture = TestBed.createComponent(AppliedJobsComponent);
        component = fixture.componentInstance;
        appliedJobsService = TestBed.inject(AppliedJobsService);
        toastrService = TestBed.inject(ToastrService);
    });
    it('should create', () => {
        expect(component).toBeTruthy();
    });
    it('should handle error and navigate to /home', () => {
        spyOn(component['_router'], 'navigateByUrl');
        appliedJobsService.getAllAppliedJobs.and.returnValue(throwError('Error'));
        component.loggedUser = { userId: 'user123' };
        component.ngOnInit();
        expect(appliedJobsService.getAllAppliedJobs).toHaveBeenCalledWith('user123');
        expect(component['_router'].navigateByUrl).toHaveBeenCalledWith('/home');
        expect(toastrService.error).toHaveBeenCalledWith('Error occurred');
    });
});
