import { Component } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import { Router } from '@angular/router';
import { EmployerJobDetailService } from './employer-job-details.service';
import { EmployerService } from '../employer-job-posting/employer-job-posting.service';
import { Status } from 'src/constants';
import { CreateProfileService } from '../create-profile/create-profile.service';
let EmployerJobDetailComponent = class EmployerJobDetailComponent {
    constructor(employerJobDetailSevice, employerSevice, toastr, router, createProfileService) {
        this.employerJobDetailSevice = employerJobDetailSevice;
        this.employerSevice = employerSevice;
        this.toastr = toastr;
        this.router = router;
        this.createProfileService = createProfileService;
        this.modal = false;
        this.candidateList = [];
        this.statusChanged = {
            userId: "",
            status: ""
        };
    }
    ngOnInit() {
        this.applicationStatuses = Status;
        this.loggedUser = localStorage.getItem("user");
        this.currentJobId = localStorage.getItem("currentJobId");
        this.loggedUser = JSON.parse(this.loggedUser);
        this.employerJobDetailSevice.getJobPostingsDetails(this.currentJobId).subscribe((data) => {
            this.job = data;
            console.warn(data);
        }, (error) => {
        });
        this.employerJobDetailSevice.getCandidateListForCurrentJob(this.loggedUser.userId, this.currentJobId).subscribe((data) => {
            this.employerJobInfo = data;
            this.candidateList.push(...this.employerJobInfo.candidates);
            console.warn(this.employerJobInfo.candidates);
        }, (error) => {
            this.toastr.error('Error' + error.message);
        });
    }
    updateJob(jobId) {
        console.log('applyToJob() called with jobId: ' + jobId);
        localStorage.setItem("currentJobId", jobId.toString());
        this.router.navigate(['update-job-posting']);
    }
    downloadResume(userId) {
        this.createProfileService.downloadResume(userId).subscribe((res) => {
            this.blob = new Blob([res], { type: 'application/pdf' });
            var downloadURL = window.URL.createObjectURL(res);
            var link = document.createElement('a');
            link.href = downloadURL;
            link.download = "resume.pdf";
            link.click();
        }, (error) => {
            this.toastr.error('Resume download failed', 'Download Failed');
        });
    }
    deleteJob(jobId) {
        console.log('deleteJob() called with jobId: ' + jobId);
        this.employerSevice.deleteEmployerJobPostings(jobId).subscribe((data) => {
            this.toastr.error('Success' + data);
        }, (error) => {
            this.toastr.error('Error' + error.message);
        });
    }
    onApplicationStatusChange(candidate, value) {
        console.log(`Application status changed for ${candidate.firstName} to ${value}`);
        this.statusChanged.userId = candidate.userId;
        this.statusChanged.status = value;
    }
    // Function to save the updated application status
    saveApplicationStatus(candidate, jobId) {
        console.log(`Saving application status ${candidate.status} for ${candidate.firstName}`);
        this.employerJobDetailSevice.updateCandidateStatus(candidate.userId, jobId, this.statusChanged.status).subscribe((data) => {
            console.log(data);
            this.toastr.success(data);
        }, (error) => {
            console.log(error);
            this.toastr.error('Error' + error.message);
        });
    }
};
